  'I am',
  $value =~ Array ? {
    default => 'not an array',
    true    => 'an array',
  },
].join(' ')

$test2 = [
  'I am still',
  ($value =~ Array) ? {
    default => 'not an array',
    true    => 'an array',
  },
].join(' ')

